    /// </summary>
    /// <param name="x"></param>
    /// <param name="y"></param>
    public TreeRect(float x, float y)
    {
        SetRect(x, y, 0, 0);
    }

    /// <summary>
    /// 四边形
    /// </summary>
    /// <param name="x"></param>
    /// <param name="y"></param>
    /// <param name="w"></param>
    /// <param name="h"></param>
    public TreeRect(float x, float y, float w, float h)
