        {
            treeAction?.Invoke(node);
            return;
        }

        if (node.Childs != null)
        {
            for (int i = 0; i < node.Childs.Count; i++)
            {
                ForEachEmptyLeafNode(node.Childs[i], treeAction);
            }
        }
    }

    private void RemoveChildNode(MapQuadBoundTree node)
    {
